import React, { useEffect } from "react";
import { connect } from "react-redux";
import { Route } from "react-router-dom";

import ProductCardContainer from "../../components/product-card/product-card.container";

import { fetchCollectionsStart } from '../../redux/shop/shop.actions'


const ProductDetailsPage = ({ fetchCollectionsStart, match }) => {

    useEffect(() => {
        fetchCollectionsStart();
    }, [fetchCollectionsStart]);


    return (
        <div className='product-details'>
        <Route 
            path={`${match.path}/:collectionId/:productId`}
            component={ProductCardContainer}
        />
        </div>
    );

};

const mapDispatchToProps = dispatch => ({
    fetchCollectionsStart: () => dispatch(fetchCollectionsStart())
});

export default connect(null, mapDispatchToProps)(ProductDetailsPage);
