import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { selectCollection } from "../../redux/shop/shop.selectors";
import { fetchCollectionsStart } from '../../redux/shop/shop.actions'


const ProductDetailsPage = ({ fetchCollectionsStart, match, collections }) => {

    const {title, items} = collections;

    useEffect(() => {
        fetchCollectionsStart();
    }, [fetchCollectionsStart]);

    console.log(match);

    const ShowProduct = () => {
        return (
            <div>
                {title}
            </div>
        )
    }

    return (
        <div className='product-details'>
            <div className="row">
                <ShowProduct />
            </div>
        </div>
    );

};

const mapDispatchToProps = (dispatch, ownProps) => ({
    fetchCollectionsStart: () => dispatch(fetchCollectionsStart()),
    collection: selectCollection(ownProps.match.params.collectionId)(dispatch)
});

export default connect(null, mapDispatchToProps)(ProductDetailsPage);
