import React from "react";

import FormInput from "../form-input/form-input.component";

import './sign-in.styles.scss';

//use class component because need to store what users typing in
class SignIn extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            email: '',
            password: ''
        }
    }

    handleSubmit = (e) => {
        e.preventDefault();

        this.setState({ email: '', password: '' }); //clear out the fields
    }

    handleChange = (e) => {
        //to pull both the value and name of our event.target
        const { value, name } = e.target;

        this.setState({ [name]: value });
    }

    render() {
        return (
            <div className='sign-in'>
                <h2>I already have an account</h2>
                <span>Sign in with your email and password</span>

                <form onSubmit={this.handleSubmit}>
                    <FormInput 
                        name="email" 
                        type="email" 
                        value={this.state.email} 
                        handleChange={this.handleChange} 
                        label='Email'
                        required 
                    />
                    <label>Email</label>
                    <FormInput name="password" type="password" value={this.state.password} handleChange={this.handleChange} required />
                    <label>Password</label>

                    <input type="submit" value='Submit Form' />
                </form>
            </div>
        );
    }
}

export default SignIn;
