import { firestore } from "../../firebase/firebase.utils";

export const handleFetchProduct = productID => {
    return new Promise((resolve, reject) => {
        firestore
            .collection('collections')
            .doc(productID)
            .get()
            .then(snapshot => {
                if(snapshot.exists) {
                    resolve(
                        snapshot.data()
                    );
                }
            })
            .catch(err => {
                reject(err);
            })
    })
}
