import React from "react";
import { connect } from "react-redux";
import { withRouter } from "react-router";

import ProductItem from "../../components/product-item/product-item.component";
import { SearchBar } from "../../components/search-bar/search-bar.component";
import CustomButton from "../../components/custom-button/custom-button.component"

import { selectCollection } from "../../redux/shop/shop.selectors";
import { addItem } from "../../redux/cart/cart.actions";
import { useState } from "react";

import SimpleImageSlider from "react-simple-image-slider";

import './product.styles.scss';

const ProductPage = ({ collection, match, history, addItem }) => {
    const { items } = collection;
    

    const image = items[match.params.itemId - 1].imageUrl;

    //convert the format to meet the slider requirement
    const images = image.map(i => ({'url': i}));
    console.log(match);
    
    return (
        <div className='product-page'>

            <div className='slider'>
                <SimpleImageSlider 
                    width={400}
                    height={400}
                    images={images}
                    showBullets={true}
                    showNavs={true}
                />
            </div>
            <div className='product-detail'>
                <h4 className='categoty'>{match.params.collectionId.toUpperCase()} / {match.params.itemId}</h4>
                <h1 className='name'>
                    {items[match.params.itemId - 1].name}
                </h1>
                <p className='desc'>
                    {items[match.params.itemId - 1].desc}
                </p>
                <h3 className='price'>
                    RM {items[match.params.itemId - 1].price.toFixed(2)}
                </h3>
                <CustomButton onClick={() => addItem(items[match.params.itemId - 1])} inverted>Add To Cart</CustomButton>
                <CustomButton onClick={() => {
                    history.push('/checkout');
                }}>
                    GO TO CART
                </CustomButton>
            </div>
        
        </div>
    )
};

const mapStateToProps = (state, ownProps) => ({
    collection: selectCollection(ownProps.match.params.collectionId)(state)
});

const mapDispatchToProps = dispatch => ({
    addItem: item => dispatch(addItem(item))
});

export default connect(mapStateToProps, mapDispatchToProps)(withRouter(ProductPage));
